e = Ask; // Исходная цена ордера double orderPrice = initialOrderPrice; // Текущая цена ордера // Проверка наличия позиции if (OrderSelect(0, SELECT_BY_POS, MODE_TRADES)) { if (OrderSymbol() == Symbol() && OrderMagicNumber() == 123456) // Проверка существующей позиции { return (0); // Если позиция уже открыта, завершаем функцию } } // Открываем новый ордер if (consecutiveLosses == 0) // Если нет последовательных проигрышей { // Открытие ордера (BUY или SELL) с первоначальной ставкой initialLotSize if (Buy) { double stopLossPrice = Ask - stopLossPips * Point; double takeProfitPrice = Ask + takeProfitPips * Point; OrderSend(Symbol(), OP_BUY, lotSize, Ask, 3, stopLossPrice, takeProfitPrice, \\"Buy Order\\", 123456, 0, Green); } else if (Sell) { double stopLossPrice = Bid + stopLossPips * Point; double takeProfitPrice = Bid - takeProfitPips * Point; OrderSend(Symbol(), OP_SELL, lotSize, Bid, 3, stopLossPrice, takeProfitPrice, \\"Sell Order\\", 123456, 0, Red); } } else if (consecutiveLosses > 0 && consecutiveLosses <= maxConsecutiveLosses) // При последовательных проигрышах { // Удвоение размера ставки и открытие нового ордера lotSize *= 2; if (Buy) { double stopLossPrice = Ask - stopLossPips * Point; double takeProfitPrice = Ask + takeProfitPips * Point; OrderSend(Symbol(), OP_BUY, lotSize, Ask, 3, stopLossPrice, takeProfitPrice, \\"Buy Order\\", 123456, 0, Green); } else if (Sell) { double stopLossPrice = Bid + stopLossPips * Point; double takeProfitPrice = Bid - takeProfitPips * Point; OrderSend(Symbol(), OP_SELL, lotSize, Bid, 3, stopLossPrice, takeProfitPrice, \\"Sell Order\\", 123456, 0, Red); } } return (0); } void OnTick() { // Проверка наличия открытых ордеров int totalOrders = OrdersTotal(); for (int i = 0; i < totalOrders; i++) { if (OrderSelect(i, SELECT_BY_POS, MODE_TRADES)) { if (OrderSymbol() == Symbol() && OrderMagicNumber() == 123456) // Проверка существующей позиции { // Проверяем закрытие позиции по стоп-лоссу или профит-цели if (OrderClosePrice() != 