uffleCards(); app = new GameTableFrame(dealer, player); app.setVisible(true); // Replace app.run(); with this line } public static void main(String[] args) { LoginDialog loginDialog = new LoginDialog(null); loginDialog.setVisible(true); if (loginDialog.isLoggedIn()) { String login = loginDialog.getLogin(); String password = loginDialog.getPassword(); GUIExample example = new GUIExample(); example.dealer = new Dealer(); example.player = new Player(login, password, 10000); example.run(); } } } package GUIExample; import Model.Dealer; import Model.HighSum; import Model.Player; import java.awt.EventQueue; import javax.swing.*; public class HighSumGUI { public static void main(String[] args) { EventQueue.invokeLater(new Runnable() { @Override public void run() { LoginDialog loginDialog = new LoginDialog(null); loginDialog.setVisible(true); if (loginDialog.isLoggedIn()) { String login = loginDialog.getLogin(); String password = loginDialog.getPassword(); HighSum highSum = new HighSum() { // Override the â€˜runâ€™ method to display and update the GameTableFrame @Override public void run() { Dealer dealer = getDealer(); Player player = getPlayer(); GameTableFrame gameTableFrame = new GameTableFrame(dealer, player); gameTableFrame.setVisible(true); // Use a loop to continuously update and start new games as desired by the user boolean carryOn = true; while (carryOn) { runOneRound(); gameTableFrame.updateScreen(); int response = JOptionPane.showConfirmDialog( gameTableFrame, \\"Do you want to play another game?\\", \\"New Game\\", JOptionPane.YES_NO_OPTION ); carryOn = response == JOptionPane.YES_OPTION; } gameTableFrame.dispose(); } }; highSum.init(login, password); highSum.run(); } } }); } } package Controller; import Model.Dealer; import Model.Player; import View.ViewController; public class GameController { private Dealer dealer; private Player player; private ViewController view; private int chipsOnTable; private boolean playerQuit; public GameController(Dealer dealer,Player player,ViewController view) { this.dealer = dealer; this.player = player; this.view = view; this.chipsOnTable = 0; } public boolean getPlayerQuitStatus() { return playerQuit; } public void run() { boolean carryOn= true; while(carryOn) { runOneRound(); char r = this.view.getPlayerNextGame(); if(r=='n') { carryOn = false; } } this.view.displayPlayerNameAndLeftOverChips(this.player); this.view.displayExitGame(); } public void runOneRound() { this.view.displayGameTitle(); this.view.displayDoubleLine(); this.view.displayPlayerNameAndChips(this.player); this.view.displaySingleLine(); this.view.displayGameStart(); this.view.displaySingleLine(); this.dealer.shuffleCards(); this.chipsOnTable = 0; boolean playerQuit = false; for(int round = 1;round<=4;round++) { this.view.displaySingleLine(); this.view.displayDealerDealCardsAndGameRound(round); this.view.displaySingleLine(); if (round == 1) { //round 1 deal extra card this.dealer.dealCardTo(this.player); this.dealer.dealCardTo(this.dealer); } else { this.dealer.dealCardTo(this.player); this.dealer.dealCardTo(this.dealer); } this.view.displayPlayerCardsOnHand(this.dealer); this.view.displayBlankLine(); this.view.displayPlayerCardsOnHand(player); this.view.displayPlayerTotalCardValue(player); int whoCanCall = this.dealer.determineWhichCardRankHigher(dealer.getLastCard(), player.getLastCard()); if(whoCanCall==1) {//dealer call int chipsToBet = this.view. getDealerCallBetChips(); //ask player want to follow? char r = this.view.getPlayerFollowOrNot(this.player,chipsToBet); if(r=='y') { this.player.deductChips(chipsToBet); this.chipsOnTable+=2*chipsToBet; this.view.displayPlayerNameAndLeftOverChips(this.player); this.view.displayBetOntable(this.chipsOnTable); }else { playerQuit = true; break; } }else {//player call if(round==1) {//round 1 player cannot quit int chipsToBet = view.getPlayerCallBetChip(this.player); this.player.deductChips(chipsToBet); this.chipsOnTable+=2*chipsToBet; this.view.displayBetOntable(this.chipsOnTable); }else { char r = this.view.getPlayerCallOrQuit(); if(r=='c') { int chipsToBet = view.getPlayerCallBetChip(this.player); this.player.deductChips(chipsToBet); this.chipsOnTable+=2*chipsToBet; this.view.displayPlayerNameAndLeftOverChips(this.player); this.view.displayBetOntable(this.chipsOnTable); }else { playerQuit = true; break; } } } } //check who win if(playerQuit) { this.view.displayPlayerNameAndLeftOverChips(this.player); this.view.displayPlayerQuit(); } else if(this.player.getTotalCardsValue()>this.dealer.getTotalCardsValue()) { this.view.displayPlayerWin(this.player); this.player.addChips(chipsOnTable); this.chipsOnTable=0; this.view.displayPlayerNameAndLeftOverChips(this.player); }else if(this.player.getTotalCardsValue()<this.dealer.getTotalCardsValue()) { this.view.displayDealerWin(); this.view.displayPlayerNameAndLeftOverChips(this.player); }else { this.view.displayTie(); this.player.addChips(chipsOnTable/2); this.view.displayPlayerNameAndLeftOverChips(this.player); } //put all the cards back to the deck dealer.addCardsBackToDeck(dealer.getCardsOnHand()); dealer.addCardsBackToDeck(player.getCardsOnHand()); dealer.clearCardsOnHand(); player.clearCardsOnHand(); } } package GUIExample; import Model.Dealer; import Model.HighSum; import Model.Player; import java.awt.BorderLayout; import javax.swing.*; import java.awt.event.ActionEvent; import java.awt.event.ActionListener; public class GameTableFrame extends JFrame { private GameTablePanel gameTablePanel; private Dealer dealer; private Player player; private JLabel shufflingLabel; private JButton playButton; private JButton quitButton; public GameTableFrame(Dealer dealer, Player player) { this.dealer = dealer; this.player = player; gameTablePanel = new GameTablePanel(dealer, player); shufflingLabel = new JLabel(\\"Shuffling\\"); shufflingLabel.setHorizontalAlignment(SwingConstants.CENTER); playButton = new JButton(\\"Play\\"); quitButton = new JButton(\\"Quit\\"); addWindowListener(new WindowAdapter() { @Overr