ubmitAnswer = (selected) => async (dispatch, getState) => { const {word, gender} = selectWord(getState()); const isCorrect = selected === (word.gender === 'm' ? 'UN/LE' : 'UNE/LA'); if (isCorrect) { dispatch(setCorrect()); const translation = await dispatch(fetchTranslation(word.word)); console.log(translation); } else { dispatch(setWrong()); let correctArticle = gender === 'masculine' ? 'LE' : 'LA'; let correctEnding = ''; for (let ending of gender === 'masculine' ? masculineResponse : femindineResponse) { if (word.endsWith(ending)) { correctEnding = ending; break; } } if (!correctEnding) { correctEnding = word; } dispatch( setCorrectArticle({ article: correctArticle, ending: correctEnding }) ); } dispatch(fetchTranslation(word)); dispatch(nextWord()); }; // async thunks export const nextWord = () => (dispatch) => { dispatch(fetchWord()); }; export default wordsSlice.reducer;","completion":"// eslint-disable-next-line no-unused-vars import { createSlice } from '@reduxjs/toolkit'; import axios from '../api/api'; const initialState = { word: null, nounList: { masculine: [], feminine: [], }, isCorrect: null, score: 0, translation: null, }; export const wordsSlice = createSlice({ name: 'words', initialState, reducers: { setWord: (state, action) => { state.word = action.payload; state.isCorrect = null; }, setNouns: (state, action) => { state.nounList = action.payload; }, setCorrect: (state) => { state.isCorrect = true; state.score++; }, setWrong: (state) => { state.isCorrect = false; }, setTranslation: (state, action) => { state.translation = action.payload; }, setCorrectArticle: (state, action) => { state.correctArticle = action.payload.article; state.correctEnding = action.payload.ending; }, }, }); export const { setWord, setNouns, setCorrect, setWrong, setTranslation, setCorrectArticle, } = wordsSlice.actions; export const selectWord = (state) => state.words.word; export const selectNouns = (state) => state.words.nounList; export const selectIsCorrect = (state) => state.words.isCorrect; export const selectScore = (state) => state.words.score; export const selectTranslation = (state) => state.words.translation; const config = { headers: { Authorization: \`Bearer { JSON.parse(localStorage.getItem('userInfo')).accessToken }\`, }, }; export const fetchWord = () => async (dispatch) => { try { const response = await axios.get('/words/random', config); dispatch(setWord(response.data)); } catch (error) { console.error(error); } }; export const fetchNouns = () => async (dispatch) => { try { const [masculineResponse, feminineResponse] = await Promise.all([ axios.get('/words/masculine', config), axios.get('/words/feminine', config), ]); dispatch( setNouns({ masculine: masculineResponse.data, feminine: feminineResponse.data, }) ); } catch (error) { console.error(error); } }; export const fetchTranslation = (word) => async (dispatch) => { try { const response = await axios.get(\`/words/example?word={word}\`, config); dispatch(setTranslation(response.data)); return response.data; } catch (error) { console.error(error); } }; export const submitAnswer = (selected) => async (dispatch, getState) => { const { word, gender } = selectWord(getState()); const isCorrect = selected === (word.gender === 'm' ? 'UN/LE' : 'UNE/LA'); if (isCorrect) { dispatch(setCorrect()); const translation = await dispatch(fetchTranslation(word.word)); console.log(translation); } else { dispatch(setWrong()); let correctArticle = gender === 'masculine' ? 'LE' : 'LA'; let correctEnding = ''; for (let ending of gender === 'masculine' ? selectNouns(getState()).masculine : selectNouns(getState()).feminine) { if (word.word.endsWith(ending)) { correctEnding = ending; break; } } if (!correctEnding) { correctEnding = word.word; } dispatch( setCorrectArticle({ article: correctArticle, ending: correctEnding }) ); } dispatch(fetchTranslation(word.word)); dispatch(nextWord()); }; export const nextWord = () => (dispatch) => { dispatch(fetchWord()); }; export default wordsSlice.reducer;"},{"prompt":"Freedom planet all characters reacts to supreme commander","completion":"Note: There are four main characters in the game Freedom Planet: Lilac, Carol, Milla, and Torque. Here is a possible reaction for each character. Lilac: (Watching a gameplay video of Supreme Commander) \\"Wow, these battles are so huge and intense! Can you imagine if we had to fight on this scale? We'd definitely need more backup!\\" Carol: (Smirking) \\"Bring it on! I bet I could take down one of those giant robots with just my motorcycle! Okay, maybe I'd need a couple of modifications... but how awesome would that be?\\" Milla: (Looking a little scared) \\"I'm not sure I'd like to be in a war like that... it seems really scary. But I guess I could try to help from a safe distance, like by repairing things or providing support.\\" Torque: (Thoughtfully) \\"You know, the technology in Supreme Commander is really advanced, and the strategy required to play the game is complex. As a galactic explorer and soldier, I'd love to learn more about how they developed these battle systems.\\""},{"prompt":"Hello there","completion":"Hello, how may I assist you?"},{"prompt":"Buzulların erimesinin neden olduğu birçok sorun olmakla birlikte, bunların başında deniz seviyesinin yükselmesi gelmektedir. Son yıllarda geçmiş yüzyıllara göre su seviyesi iki kat hızlı yükselmektedir. Bunun sebebi iklim değişikliği kaynaklı buzul kütlelerinin erimesidir. 500 yıllık sürede deniz seviyesinde görülen değişim, günümüzde 50 yıl kadar kısa bir zamanda beklenmektedir (Gökpınar, 2020: 48). Erimenin gerçekleştiği ve dünyaya en fazla zarar vereceği tahmin edilen erimelerden biri Grönland’da yaşanmaktadır. Buzulların on iki de biri Gröndland’da bulunmaktadır. Her geçen gün Grönland, 100 milyar tondan fazla buz kaybetmekte ve okyanusa karışan bu buz kütleleri deniz seviyesini yükseltmektedir. Yok olma tehlikesiyle karşı karşıya olan Grönland’ın tamamen erimesi deniz seviyesinin 7 metre yükselmesi anlamına gelmektedir (Witze’denakt. Gökpınar, 2020: 49). İklim sistemindeki birçok değişiklik, artan küresel ısınmayla ile birlikte daha büyük ve önemli bir konu haline gelmektedir. Bu değişiklikler; aşırı sıcakların, denizlerin içindeki sıcaklık artışlarının ve yoğun yağışların, bazı bölgelerdeki tarımsal ve çevreyle ilgili kuraklıkların ve yoğun tropik siklonların oranındaki artışların yanı sıra Arktik deniz buzu, kar örtüsü ve permafrosttaki azalmaları kapsamaktadır. (IPCC, 2021) Atmosferin her 1°C ısınması, atmosferdeki su buharının %7 artmasına, atmosferdeki fazla su buharı ise yağış rejimlerinin değişmesine neden olmaktadır (Kurnaz, 2021). Devam eden küresel ısınmanın, değişkenliği, küresel muson yağışları ile yağış ve kuraklık olaylarının ciddiyeti dahil olmak üzere küresel su döngüsünü daha da yoğunlaştırması bekleniyor (IPCC, 2021). Bu nedenle, bir yandan kuraklıklar olurken, diğer yandan da şiddetli yağışlar artacaktır. Bu iki nokta ilk bakışta birbirine ters gibi görünse de, basitçe şu şekilde açıklanabilir: atmosferden yere düşecek yağmur miktarı çok değişmeyecek, ama kuraklıklar uzadığı için sonunda gelen yağışta da çok fazla su yeryüzüne inecektir (Kurnaz, 2021). Geçmişteki ve gelecekteki sera gazı salımlarından kaynaklanan birçok değişiklik, özellikle okyanus, buz tabakaları ve küresel deniz seviyesindeki değişiklikler, yüzyıllardan bin yıllara kadar bir süre içerisinde geri döndürülemeyecektir (IPCC, 2021). Küresel deniz seviyeleri 1900’den bu yana yaklaşık 0,2 m yükselmiştir (Rahmstorf, 2007:368). Bununla birlikte, gelecekteki küresel ortalama deniz seviyesi yükselişi tahminleri büyük ölçüde değişmektedir. 2100 yılı için yapılacak tahminler, gelecekteki sera gazı emisyonları, termal genleşme, buzulların erimesi ve Antarktika ve Grönland buz tabakaları ve buz tabakaları için izostatik ayarlama gibi birçok değişkenin hesaplanmasını gerektirir (Kopp vd., 2014:390). Deniz seviyesinin yükselmesi riski ile karşı karşıya olan çoğu yer, kalıcı olarak sular altında kalmadan çok önce, afetlerle karşı karşıya kalacaktır. Ulusal Okyanus ve Atmosfer Dairesi (NOAA), ABD kıyı bölgelerinde rahatsız edici sellerin 50 yıl öncesine göre günümüze kıyasla %300 ila %900 arasında daha sık görüldüğünü belirtmektedir (Iavarone ve Kaya, 2021:55). Hükümetlerarası İklim Değişikliği Paneli (IPCC, 2019) raporuna göre, küresel ortalama deniz seviyesindeki artışın önceki öngörülerin aksine 10 cm daha yükselerek 1,1 metre seviyelerine kadar artması beklenmektedir (IPCC, 2019). IPCC’nin değerlendirmelerine göre, küresel iklim değişimine bağlı olarak Türkiye’de deniz seviyesinin 74 cm artması beklenmektedir(IPCC, 2019). Bu artış üzerinden gerçekleştirilen gelecek senaryolarında, Türkiye’de sadece İstanbul ve İzmir’de 50 cm yükselmesi durumunda bile 252.000 kişinin taşkınlara maruz kalması öngörülmektedir (WWF, 2021). Deniz seviyesindeki değişikliğin önemli bir boyutu, belirsizliğini korumaktadır çünkü bu etki azaltma çabalarının amacına, dikkate alınan yere ve zamana bağlıdır. Küresel ortalama sıcaklığın +5°C’ye ulaşabileceği yüksek emisyon senaryosunda, deniz seviyesindeki artış 178 cm’ye ulaşabilir. Dahası, termal genleşme ve büyük buz tabakalarının katkısı hesaba katılırsa, deniz seviyesi 2100’de 2 m’yi bile aşabilir (Bamber vd., 2019: 11195). Bu artışlar göz önüne alındığında, özellikle dikkat edilmesi gereken durum, deniz seviyesinde meydana gelmesi muhtemel her 1 metrelik artışın denizin kıyıdan 100 metre içeriye kadar taşmasını ifade etmektedir (Kurnaz, 2019). Sonuç olarak, deniz seviyesinin yükselmesi sorununun, küresel iklim değişikliğinin gelecekteki en pahalı ve geri döndürülemez sonuçlarından biridir. Gelecekte dünya toplam 600 milyonu aşan küresel kıyı nüfusuyla (bu yüzyılda bir milyar insanı aşması beklenmektedir) karşı karşıya kalacaktır (Neumann vd., 2015: 18) ve bu durumun maliyetinin küresel gayri safi yurtiçi hasılanın %4,5’ine ulaşması beklenmektedir (Desmet vd., 2021:444) Deniz seviyesinin yükselmesi nedeniyle göç etmek durumunda kalacak olan insanların sayısının tahmini 88 milyon ile 1,4 milyar arasında değişmektedir. Bu tahminler arasındaki büyük fark kimin “risk altında” olduğuna dair farklı tanımlardan kaynaklanmaktadır (Hauer vd., 2020:29). Risk altındaki kişilerin en yaygın tanımları şu şekilde yapılmıştır; 1. Düşük rakımlı kıyı bölgesinde yaşayan nüfuslar, (Düşük rakımlı kıyı bölgesi; yüksekliği 10 m’nin altında ve bazen kıyıdan 100 km’lik mesafe içinde olan alan olarak tanımlanır.) 2. 100 yıllık taşkın yatağında yaşayan nüfuslar ve 3. Seçilen deniz seviyesi yükselmesi senaryolarında sular altında kalacak alanlarda yaşayan nüfuslar. Her üç yaklaşımın da deniz seviyesi yükselmesi ve göç arasındaki bağlantıları anlamak için güçlü yanları, zayıf yönleri ve çıkarımları vardır (Hauer vd., 2020: 32). Amerika, Bangladeş, Atol adaları, Çin, Hindistan, Endonezya, Vietnam, Mısır ve Nijerya başta olmak üzere birçok ülke deniz seviyesindeki artış nedeniyle yüksek tehdit altındadır. ABD nüfusunun yaklaşık %40’ı şu anda gelecekte de büyüme ve gelişmenin devam edeceği tahmin edilen kıyı topluluklarında yaşamaktadır (Crossett, 2013:2). Deniz seviyesinin yükselmesi ve yine bu nedenle sel ve taşkınlardaki artışın Amerika’da 2100 yılına kadar 3 milyon ile 43 milyon arasında insanı tehdit etmesi ve koruyucu önlemler alınmadan 13 milyonunun kalıcı su baskını ve yerinden edilme ile karşı karşıya kalabileceği öngörülmektedir. Bangladeş, 2 milyon ila 110 milyon insanın deniz seviyesindeki artışla bağlantılı olarak risk altında olduğu için en riskli üçüncü ülkedir. Atoll adası ülkeleri ise milyonlarca insanın deniz seviyesindeki artış riski tehdidi altında olduğu ABD ve Bangladeş’in aksine, seyrek nüfuslu atol adası ülkeleri, nispeten daha az nüfusludur (Neumannvd, 2015). Ancak tamamen vatansız kalma durumu ve nispeten daha yakın bir zamanda göç etmek durumunda kalacak olması nedeniyle önemlidir. Deniz seviyesindeki artış riski, en alçakta bulunan ülkeler için o kadar şiddetli ki, yersiz yurtsuzlaşma olasılığı, birçok atol adası ülkesi için bu konudaki söylemlerin çoğunu ele geçirmiş durumdadır. Birleşmiş Milletler Mülteciler Yüksek Komiserliği 10 yıl önce Maldivler, Tuvalu, Kiribati ve Marshall Adaları gibi alçak atoll ulus devletlerinde vatansızlığı önlemek için erken önlem alınması gerektiğini belirtmiştir. Bütün bir bölgenin kaybedilmesi veya bütün bir nüfusun sürgün edilmesi emsali görülmemiştir (UNHCR, 2009: 1). Bu durum, bir devletin batık olması halinde varlığının sona ereceği belirsiz olsa bile, benzeri görülmemiş bir devlet çözülme ve olası vatansızlık senaryolarını ortaya koymaktadır (Kälin vd., 2012: 27). Bu beklenen vatansızlaşma veya önemli toprak kaybı, diğer konuların yanı sıra devlet olma, ulusal kimlik, mülteci statüsü, devletin sorumluluğu ve kaynaklara erişim ile ilgili yasal endişeleri beraberinde getirmektedir. Su baskını önemli bir endişe olsa da, atoll ülkeleri, tatlı . su eksikliği ve artan toprak tuzlanması nedeniyle tamamen su altında kalmadan önce yaşanmazlıkla karşı karşıya kalacaktır. bu makalenin farklı kelimeler ile uzunca ifadeleri.","completion":"Buzulların erimesi, ciddi bir soruna yol açmaktadır. Bunun en büyük nedeni, deniz seviyesinin yükselmesidir. Geçmiş yıllara göre deniz seviyesi iki kat hızlı yükselmektedir ve bu durum iklim değişikliği kaynaklı buzul kütlelerinin erimesi ile ilgilidir. En büyük risklerden biri Grönland'da yaşanan erime olduğu düşünülmektedir. Her gün Grönland, 100 milyar tondan fazla buz kaybetmektedir ve bu buz kütleleri deniz seviyesinin yükselmesine neden olmaktadır. Artan küresel ısınma, birçok soruna yol açmaktadır. Aşırı sıcakların, denizlerdeki sıcaklık artışlarının, yoğun yağışların, tarımsal ve çevresel kuraklıkların ve tropik siklonların oranındaki artışlar gibi değişiklikler yaşanmaktadır. Atmosferdeki her 1°C'lik artış, atmosferdeki su buharının %7 artmasına neden olur ve yağış rejimlerinin değişmesine yol açar. Gelecekte küresel iklim değişimi, su döngüsünü daha da yoğunlaştırarak kuraklıkların ve şiddetli yağışların artacağı öngörülmektedir. Deniz seviyesindeki artışın öngörülemezliği nedeniyle, azaltma çabalarına ve zaman ve yer faktörlerine bağlıdır. Küresel ortalama sıcaklığın +5°C'ye ulaşması yüksek emisyon senaryosunda deniz seviyesindeki artış 178 cm'ye kadar çıkabilir. Bu artışlar sonucunda, deniz seviyesinin her 1 metrelik artışı, denizin kıyıdan 100 metre içeriye kadar taşmasına neden olacaktır. Deniz seviyesindeki artış nedeniyle göç etmek durumunda kalacak insanların sayısı 88 milyon ile 1,4 milyar arasında değişmektedir. Bu durum gelecekte dünya toplam 600 milyonu aşan küresel kıyı nüfusuyla karşı karşıya kalacak demektir. Deniz seviyesindeki artışın maliyeti, küresel gayri safi yurtiçi hasılanın %4,5'ine kadar ulaşabilir. Risk altındaki kişiler genellikle düşük rakımlı kıyı bölgelerinde yaşayan nüfuslar, 100 yıllık taşkın yatağında yaşayan nüfuslar ve sular altında kalacak alanlarda yaşayan nüfuslar olarak tanımlanabilir. Amerika, Bangladeş, Atol adaları, Çin, Hindistan, Endonezya, Vietnam, Mısır ve Nijerya başta olmak üzere birçok ülke deniz seviyesindeki artış nedeniyle yüksek tehdit altındadır. Sonuç olarak, deniz seviyesinin yükselmesi sorunu, gelecekteki en pahalı ve geri döndürülemez sonuçları"},{"prompt":"Palabras para vocaulario de auditoria. - 監査 (かんさ/kansa): Auditoria - 会計 (かいけい/kaikē): Contabilidad - 決算書 (けっさんしょ/kessansho): Estados financieros - 損益計算書 (そんえきけいさんしょ/son’ekikēsansho): Estado de resultados - 貸借対照表 (たいしゃくたいしょうひょう/taishakutaishōhyō): Balance general - 内部統制 (ないぶとうせい/naibutōsei): Control interno - リスク管理 (リスクかんり/risukukanri): Gestión de riesgos - 目標管理 (もくひょうかんり/mokuhōkanri): Gestión de objetivos - 信頼性 (しんらいせい/shinraisei): Fiabilidad - 証拠の調査 (しょうこのちょうさ/shōkonochōsa): Investigación de evidencia - 関与する者 (かんよするもの/kanyosurumono): Partes relacionadas - 分析 (ぶんせき/bunseki): Análisis - 組織文化 (そしきぶんか/soshikibunka): Cultura organizacional - 内部通報 (ないぶつうほう/naibutsuhō): Informe interno - マネジメント意思決定 (マネジメントいしけってい/manejimentoishikettei): Toma de decisione