s[i].cache[j] = -1; nodes[i].cache_age[j] = -1; // Initialize age of each item to -1 } node_activity(client); } schedule(START, 0.0); schedule(END, SIMULATION_TIME); simulate(); printf(\\"Cache hit ratio: %.2fn\\", cache_hits / total_queries); printf(\\"Query delay: %.2fn\\", tbl_mean(query_delay)); printf(\\"n\\"); } } // Clean up table_rm(query_delay); table_rm(cache_hit_ratio); err = facility_rm(server); handle_csim_error_message(err); cleanup(); free(nodes); return 0; } void client() { node *n = nodes[node_id()]; while (clock < SIMULATION_TIME) { hold(exponential(n->q_gen_time)); float prob = lrand48() / (float)RAND_MAX; // Hot data item or cold data item int data_item; if (prob < hot_access_prob) { data_item = lrand48() % NUM_HOT_ITEMS + 1; } else { data_item = NUM_HOT_ITEMS + (lrand48() % (DB_SIZE - NUM_HOT_ITEMS)); } generate_query(n); } } void generate_query(node *n) { request(server); double q_start_time = clock; hold(DATA_ITEM_SIZE * 8 / BANDWIDTH); int cache_hit = is_item_in_cache(n, data_item); if (cache_hit) { cache_hits++; release(server); // Send a check message to the server request(server); hold(DATA_ITEM_SIZE * 8 / BANDWIDTH); // Receive confirm message from the server release(server); } else { // Request data item from the server release(server); query_result(n); } // Calculate query delay double q_delay = clock - q_start_time; sample(query_delay, q_delay); total_queries++; } void query_result(node *n) { hold(exponential(n->u_arr_time)); // Probability of hot data item update float prob = lrand48() / (float)RAND_MAX; if (prob < hot_update_prob) { request(server); hold(DATA_ITEM_SIZE * 8 / BANDWIDTH); // Update hot data item release(server); } // Update the cache based on the LRU policy update_cache(n, data_item); } int is_item_in_cache(node *n, int item) { int item_found = 0; for (int i = 0; i < CACHE_SIZE; i++) { if (n->cache[i] == item) { n->cache_age[i] = 1; item_found = 1; } else { n->cache_age[i]++; } } return item_found; } void handle_csim_e