R1 = 0x0001; //enable timer NVIC_EnableIRQ(TIM2_IRQn); } //--------------------------------------------------------------- void TIM2_IRQHandler(void){ TIM2->SR &= ~0x00000001; // This line clears the TIM2 interrupt flag. // send data as normal satae ------ if(flag1==1){ DAC(RamReader()); } //----------------------------------- // recieve data after intrupt ------ else{ RamWriter(ADC()); Counter++; if(Counter==SampleNumber){ flag1=1; Counter=0; address=0; EXTI->IMR = (1<<1); } } //----------------------------------- } // ---------------------------------------------------------- void GenaralSramSetting(void){//The GenaralSramSetting function initializes the external SRAM GPIOB->ODR |= (0x0010);//PB4=1 --which deactivates the chip select signal for the external SRAM. //This line of code ensures that the chip select signal is inactive before sending the write command to the SRAM. GPIOB->ODR &= ~(0x0010);//PB4=0--which activates the chip select signal for the SRAM. // This line of code is used to select the SRAM before sending any read or write commands to it. SPI1->DR = 0x01;//WRSR 0000 0001 Write STATUS register //Write STATUS register// This line sets the data register of the SPI1 peripheral to 0x01, which corresponds to the command to write to the SRAM status register. while((SPI1->SR & SPI_SR_BSY) == SPI_SR_BSY); // sr-> flags //This is a busy-wait loop that waits for the SPI peripheral to finish transmitting data before proceeding to the next line of code. SPI1->DR = 0x40; //Sequential mode 0100_0000//This line sets the data register of the SPI1 peripheral to 0x40, which sets the sequential mode of the SRAM to 0x40. This corresponds to the mode in which the SRAM sends/receives a sequence of data bytes without requiring re-sending the address, which is useful in applications where large amounts of contiguous data are being read/written. while((SPI1->SR & SPI_SR_BSY) == SPI_SR_BSY); // sr-> flags //This is another busy-wait loop that waits for the SPI peripheral to finish transmitting data before proceeding to the next line of code. GPIOB->ODR |= (0x0010);//This line sets GPIO pin B4 high, which deactivates the chip select signal for the SRAM. This is necessary to complete the initialization of the SRAM and return it to its inactive state. } //------------------------------------------------------------------------------------------- void spi1Init(void){ RCC->APB2RSTR |= RCC_APB2RSTR_SPI1RST; RCC->APB2RSTR &= ~RCC_APB2RSTR_SPI1RST; RCC->APB2ENR |= RCC_APB2ENR_SPI1EN; // faal sazi clk SPI1->CR1 = 0x0044; //0100_0100 // bit 6 : spi anable // bit 2 : mster selection SPI1->CR2 = 0x0004; // 0000 0100//configures the SPI1 peripheral to automatically control the NSS pin during data transmission/reception, allowing the microcontroller to use hardware management of the slave select signals rather than software management. } //--------------------------------------------------------------------- uint16_t ADC(void){ // meghdar motale shode ra barmigardanad uint16_t a; GPIOA->ODR &= ~(0x0002); // select ADC as slave SPI1->DR = 0x06;//instructs the ADC to start the conversion process. while((SPI1->SR & SPI_SR_BSY) == SPI_SR_BSY); SPI1->DR = 0x40;//This line of code sends a command to the ADC using the SPI1 interface to start an analog to digital conversion for a specific input channel. while((SPI1->SR & SPI_SR_BSY) == SPI_SR_BSY); // 4 bit vazn bala a=((SPI1->DR & 0x000f)<<8); SPI1->DR = 0x00; while((SPI1->SR & SPI_SR_BSY) == SPI_SR_BSY); // 8 bit vazn paeen a |= (SPI1->DR & 0x00ff); GPIOA->ODR |= (0x0002); return(a); } //-------------------------------------------------------------------- void DAC(uint16_t d){ uint16_t s; s = (1<<12) | (d&0x0fff); //Gain=2, Shutdown=0 // 0001(d) GPIOA->ODR &= ~(0x0008); // 0 kardan ss SPI1->DR = ((s>>8)&0xff); // 4 bit ersal moshvad while((SPI1->SR & SPI_SR_BSY) == SPI_SR_BSY); SPI1->DR =(s&0xff); // 8 bit ersal while((SPI1->SR & SPI_SR_BSY) == SPI_SR_BSY); SPI1->DR; // baray etmanan GPIOA->ODR |= (0x0008